import React from 'react';
import './App.css';
import Pokemons from './data';
import ListPokemons from './ListPokemons'

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {index: 0, filter: "all"};
  }

  setFilterPokemon () {
    const {pokemons} = this.props;
    console.log(pokemon);
  }

  render() {
    return (
      <div className="App">
          <br></br>
          <button onClick={this.setFilterPokemon}>Próximo pokémon</button>


          <div>
          <ListPokemons pokemon = {
            Pokemons.filter((pokemon) => pokemon === this.state.filter)
            .map(pokemon => pokemon)
          }/>
          </div>
      </div>
    );
  }  
}

export default App;
