import React from 'react';
import './App.css';
import Pokemons from './data';
import ListPokemons from './ListPokemons'

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {filter: undefined};
  }

  setFilterValue = event => {
    this.setState({
      filter: event.target
    })
    console.log(event.target);
  }

  render() {
    return (
      <div className="App">
          <br></br>
          <button onClick={this.setFilterValue}>Próximo pokémon</button>


          <div>
          <ListPokemons pokemon = {
            Pokemons.filter((pokemon) => pokemon === this.state.filter)
            .map(pokemon => pokemon)
          }/>
          </div>
      </div>
    );
  }  
}

export default App;
