import React from "react";

class Formulario extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            name: '',
            email:'',
            cpf:'',
            endereço:'',
            cidade:'',
            estado:'',
            tipo:''
        }
    }

    trocarInput = event => {
        console.log(event.target.value);
        const { name} = event.target;
        this.setState((state) =>({
          [name]: this.value  
        }))
    }


    render() {
        return (
            <div>
                <h1>Cadastro de Currículo</h1>
                <div>
                    <p>Nome completo:  <input className="text-input" type="text" name="name" value={this.state.name}
                    onChange={this.trocarInput} maxLength="40" required/></p>
                    <p>Email: <input className="text-input" type="email" name="email" value={this.state.email}
                    onChange={this.trocarInput} maxLength="50" required/></p>
                    <p>CPF: <input className="text-input" type="text" name="cpf" value={this.state.cpf}
                    onChange={this.trocarInput} maxLength="11" required/> </p>
                    <p>Endereço: <input className="text-input" type="text" name="endereço" value={this.state.endereço}
                    onChange={this.trocarInput} maxLength="200" required/> </p>
                    <p>Cidade: <input className="text-input" type="text" name="cidade" value={this.state.cidade}
                    onChange={this.trocarInput} maxLength="28" required/> </p>
                    <p>Estado: <input className="text-input" type="ComboBox" name="estado" value={this.state.estado}
                    onChange={this.trocarInput} required/> </p>
                    <p>Casa: <input type="radio" name="tipo" value={this.state.casa}
                    onChange={this.trocarInput} required/>
                    Apartamento: <input type="radio" name="tipo" value={this.state.Apartamento}
                    onChage={this.trocarInput} required/> </p>
                </div>
            </div>
        );
    }
}

export default Formulario
