class Formulario extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            name: '',
        }
    }

    trocarInput = event => {
        console.log(event.target.value);
        const { name } = event.target;
        this.setState((state) =>({
          [name]: this.value  
        }))
    }


    render() {
        return (
            <div>
                <p>Nome completo:  <input class="text-input" type="text" name="name" value={this.state.name}
                onChange={this.trocarInput} maxLength="40" /></p>
            </div>
        );
    }
}

export default Formulario
