import React from "react";

class Formulario extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            name: '',
            email:'',
            CPF:'',
            endereço:'',
            cidade:'',
            estado:'',
            tipo:''
        }
    }

    trocarInput = event => {
        console.log(event.target.value);
        const { name} = event.target;
        this.setState((state) =>({
          [name]: this.value  
        }))
    }


    render() {
        return (
            <div>
                <p>Nome completo:  <input class="text-input" type="text" name="name" value={this.state.name}
                onChange={this.trocarInput} maxLength="40" /></p>
                <p>Email: <input class="text-input" type="email" name="email" value={this.state.email}
                onChange={this.trocarInput} maxLength="50" /></p>
                <p>CPF: <input class="text-input" type="text" name="CPF" value={this.state.CPF}
                onChage={this.trocarInput} maxLength="11" /> </p>
                <p>Endereço: <input class="text-input" type="text" name="endereço" value={this.state.endereço}
                onChage={this.trocarInput} maxLength="200" /> </p>
                <p>Cidade: <input class="text-input" type="text" name="CPF" value={this.state.cidade}
                onChage={this.trocarInput} maxLength="28" /> </p>
                <p>Estado: <input class="text-input" type="ComboBox" name="estado" value={this.state.estado}
                onChage={this.trocarInput} /> </p>
                <p>Tipo: <input class="text-input" type="radio" name="casa" value={this.state.casa}
                onChage={this.trocarInput} /> </p>
            </div>
        );
    }
}

export default Formulario
